            if (this.bottom < space) {

                this.bottom = space;

            }

        }

        else if (edge == RectangleEdge.LEFT) {

            if (this.left < space) {

                this.left = space;

            }

        }

        else if (edge == RectangleEdge.RIGHT) {

            if (this.right < space) {

